import {Swiper, SwiperSlide} from "swiper/react";
import "swiper/swiper.min.css";
import "swiper/components/effect-coverflow/effect-coverflow.min.css";
import "swiper/components/pagination/pagination.min.css";
import "./styles.css";
import SwiperCore, {EffectCoverflow, Pagination} from "swiper/core";

SwiperCore.use([EffectCoverflow, Pagination]);

export default function App() {
  const news = [
    {
      title: "LOKAPALA MINOR LEAGUES",
      category: "Events",
      updates: "FEB 22",
      date: "12/15/2021",
      image: "images/heroes.png",
    },
    {
      title: "LOKAPALA MINOR LEAGUES",
      category: "Events",
      updates: "MAR 22",
      date: "12/15/2021",
      image: "images/heroes.png",
    },
    {
      title: "LOKAPALA MINOR LEAGUES",
      category: "Events",
      updates: "APR 22",
      date: "12/15/2021",
      image: "images/heroes.png",
    },
    {
      title: "LOKAPALA MINOR LEAGUES",
      category: "Events",
      updates: "MEI 22",
      date: "12/15/2021",
      image: "images/heroes.png",
    },
    {
      title: "LOKAPALA MINOR LEAGUES",
      category: "Events",
      updates: "JUN 22",
      date: "12/15/2021",
      image: "images/heroes.png",
    },
    {
      title: "LOKAPALA MINOR LEAGUES",
      category: "Events",
      updates: "AUG 22",
      date: "12/15/2021",
      image: "images/heroes.png",
    },
    {
      title: "LOKAPALA MINOR LEAGUES",
      category: "Events",
      updates: "SEP 22",
      date: "12/15/2021",
      image: "images/heroes.png",
    },
  ];
  return (
    <div className="container">
      <div className="title_wrapper"></div>
      <Swiper
        centeredSlides={false}
        slidesPerView={3}
        spaceBetween={0}
        coverflowEffect={{
          rotate: 50,
          stretch: 0,
          depth: 100,
          modifier: 1,
          slideShadows: true,
        }}
        pagination={{
          clickable: true,
        }}
        className="mySwiper"
      >
        <SwiperSlide>
          <div className="card">
            {news.map((item, index) => (
              <img src={item.iamge} alt="" />
            ))}
          </div>
        </SwiperSlide>
        <SwiperSlide>
          <div className="card">
            <img src="images/heroes.png" alt="" />
          </div>
        </SwiperSlide>
        <SwiperSlide>
          <div className="card">
            <img src="images/heroes.png" alt="" />
          </div>
        </SwiperSlide>
        <SwiperSlide>
          <div className="card">
            <img src="images/heroes.png" alt="" />
          </div>
        </SwiperSlide>
        <SwiperSlide>
          <div className="card">
            <img src="images/heroes.png" alt="" />
          </div>
        </SwiperSlide>
        <SwiperSlide>
          <div className="card">
            <img src="images/heroes.png" alt="" />
          </div>
        </SwiperSlide>
        <SwiperSlide>
          <div className="card">
            <img src="images/heroes.png" alt="" />
          </div>
        </SwiperSlide>
      </Swiper>
    </div>
  );
}
